import { useSelector, useDispatch } from 'react-redux';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { uiActions } from './store/ui-slice';
import { Fragment, useEffect, useState} from 'react'
import Notification from './components/UI/Notification';

function App() {
  const showCart = useSelector(state => state.ui.cartIsVisible)
  const dispatch=useDispatch()
  const cart= useSelector(state=> state.cart)  
  const notification = useState(state=>state.ui.notification)

  useEffect(()=>{
    const sendCartData= async () =>{

      dispatch(uiActions.showNotifications({
        status:'pending',
        title: 'Sending',
        message:'Sending cart details'
      }))
      const response = await fetch('https://react-http-d376f-default-rtdb.firebaseio.com/cart.json',
        {
          method: 'PUT',
          body: JSON.stringify(cart)
      })
      if(!response.ok){
        throw new Error('Sending cart data fail')
      }
      dispatch(uiActions.showNotifications({
        status:'success',
        title: 'Success',
        message:'Sent cart data successfully'
      })
    )
  };

  sendCartData().catch((error) =>{
    dispatch(uiActions.showNotifications({
          status:'error',
          title: 'Error',
          message:'Sending cart data failed'
        })
      )
    });

  },[cart,dispatch])

  return (
    <Fragment>
      {notification && <Notification stat />}
      <Layout>
      {showCart && <Cart />}
      <Products />
      </Layout>

    </Fragment>

  );
}

export default App;
